}

/* Background and Variables */
.user-css-presentation {
    /* Fonts */
    --fontScript: Jost;
    --fontHeader: Noto Sans KR;
    --fontTitle: Poppins;

    /* Colours */
    --fontColour: #030861;
    --fontColourLight: #cccccc;
    --titleColour: #940606;
    --headerColour: #134611;
    --highlightColour: #58c71c;
    --highlightColourTransparent: #58c71c73;
    --backgroundColourReference: #AB9879;

    /* Constants */
    --paddingBase: 12px;
    --borderRadius: 5px;
    --border: 3px solid;

    /* Background */
    background: url(https://worldanvil.com/i/5599712/render);
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center center;
    background-attachment: fixed;
}

.user-css.page,
.user-css-extended .page,
.user-css-presentation .tooltipster-content {
    background: url(https://www.worldanvil.com/i/5606760/render);
    background: url(https://www.worldanvil.com/i/5621729/render);
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center center;
    background-attachment: fixed;
    padding: 0;
}

.user-css.page-world-homepage {
    padding: calc(var(--paddingBase)*2);

    h3 {
        margin-top: var(--paddingBase);
    }
    .row.cover {
        margin: calc(var(--paddingBase)*-2);
        margin-bottom: var(--paddingBase);
    }
}

.user-css #content {
    margin-left: var(--paddingBase);
    margin-right: var(--paddingBase);
}

.user-css-extended {
    padding-top: var(--paddingBase);
    padding-bottom: calc(var(--paddingBase)*2);
}

.user-css-extended #comments {
    padding: var(--paddingBase);
}

.user-css-presentation #full-layout {
    margin: 0px;
}

.user-css,
.user-css p,
.user-css-extended,
.user-css-extended p,
.user-css-map-sidebar p,
.user-css .calendar-presentation sup,
.user-css .timeline li .timeline-panel p,
.user-css .timeline li .milestone-panel p,
.user-css-extended dl,
.user-css-presentation .tooltipster-content,
.user-css-presentation .tooltipster-content p,
.user-css-presentation .tooltipster-sidetip.tooltipster-light .tooltipster-content,
.user-css-presentation .tooltipster-sidetip.tooltipster-light .tooltipster-content p {
    color: var(--fontColour);
    font-family: var(--fontScript);
    font-size: 1.2em;
    line-height: 1.2em;
}

/* Headers */
.user-css :is(h1),
.user-css-map-sidebar :is(h1),
.user-css-extended .page :is(h1),
.user-css-extended .orphan-era :is(h1),
.user-css-presentation .tooltipster-content :is(h1,h2) {
    color: var(--titleColour);
    font-family: var(--fontTitle);
    font-weight: 500;
    font-size: 3.5em;
    padding-bottom: calc(var(--paddingBase)/2);
    text-align: center
}

.user-css :is(h2, h3, h4, h5, h6),
.user-css-extended .page :is(h2, h3, h4, h5, h6),
.user-css-map-sidebar :is(h2, h3, h4, h5, h6),
.user-css-presentation .tooltipster-content :is(h3, h4, h5, h6) {
    font-family: var(--fontHeader);
    font-weight: 500;
    color: var(--headerColour);
    margin-bottom: 0px;
}

.user-css :is(h2),
.user-css-extended .page :is(h2),
.user-css-map-sidebar :is(h2) {
    font-size: 2em;
    margin-bottom: calc(var(--paddingBase)/2);
}

.user-css :is(h3),
.user-css-extended .page :is(h3),
.user-css-map-sidebar :is(h3),
.user-css-presentation .tooltipster-content :is(h3) {
    font-size: 1.75em;
    margin-bottom: calc(var(--paddingBase)/2);
}

.user-css :is(h4),
.user-css-extended .page :is(h4),
.user-css-map-sidebar :is(h4),
.user-css .timeline li .timeline-panel :is(h4),
.user-css .timeline li .milestone-panel :is(h4),
.user-css-extended .timeline li .timeline-panel :is(h4),
.user-css-extended .timeline li .milestone-panel :is(h4),
.user-css-presentation .tooltipster-content :is(h4) {
    font-size: 1.4em;
}

.user-css :is(h5),
.user-css-extended .page :is(h5),
.user-css-map-sidebar :is(h5),
.user-css .timeline li .timeline-panel :is(h5),
.user-css .timeline li .milestone-panel :is(h5),
.user-css-extended .timeline li .timeline-panel :is(h5),
.user-css-extended .timeline li .milestone-panel :is(h5),
.user-css-presentation  .tooltipster-content :is(h5) {
    font-size: 1.25em;
}

.user-css h6 {
    font-size: 1.1em;
}

/* Links */
.user-css :is(a),
.user-css .artist-credits :is(a),
.user-css .article-subheading :is(a),
.user-css .timeline li .timeline-panel :is(a),
.user-css .timeline li .milestone-panel :is(a),
.user-css-extended .page :is(a),
.user-css-extended .timeline li .timeline-panel :is(a),
.user-css-extended .timeline li .milestone-panel :is(a),
.user-css-presentation .tooltipster-content :is(a),
.user-css .world-menu :is(a),
.user-css .category-toc-loader :is(a),
.user-css .world-toc-loader :is(a) {
    color: var(--titleColour);
    font-style: inherit;
    text-decoration: none;
}

.user-css :is(a):hover,
.user-css :is(a):hover b,
.user-css .artist-credits :is(a):hover,
.user-css .article-subheader :is(a):hover,
.user-css .timeline li .timeline-panel :is(a):hover,
.user-css .timeline li .milestone-panel :is(a):hover,
.user-extended .page :is(a):hover,
.user-css-extended .timeline li .timeline-panel :is(a):hover,
.user-css-extended .timeline li .milestone-panel :is(a):hover,
.user-css-presentation .tooltipster-content :is(a):hover,
.user-css .world-menu :is(a):hover,
.user-css .category-toc-loader :is(a):hover,
.user-css .world-toc-loader :is(a):hover {
    color: var(--headerColour);
    text-decoration: none;
    transition: color 0.2s;
}

/* Tooltip */
.user-css-presentation .tooltipster-sidetip.tooltipster-top .tooltipster-box,
.user-css-presentation .tooltipster-sidetip.tooltipster-bottom .tooltipster-box {
    border: 10px solid transparent; 
    border-image-source: url(https://worldanvil.com/i/5599712/render);
    border-image-slice: 30 fill;
    border-image-outset: 0;
}

.user-css-presentation .tooltipster-base .tooltipster-arrow .tooltipster-arrow-border,
.user-css-presentation .tooltipster-base .tooltipster-arrow .tooltipster-arrow-background {
    border: none;
}

.user-css .text-abbreviation {
    text-decoration-color: var(--titleColour);
    text-decoration-thickness: 3px;
    border: none;
}

/* Misc */
.user-css .article-subheading,
.user-css .article-title-author {
    text-align: center;
    font-style: italic;
}

.user-css .article-title {
    margin-bottom: var(--paddingBase);
}

.user-css .text-danger {
    color: var(--headerColour) !important;
}

.user-css-extended .text-danger {
    color: var(--titleColour) !important;
}

.user-css hr,
.user-css-extended hr {
    border: var(--border) var(--titleColour);
    opacity: 0.7;
    border-radius: var(--borderRadius);
}

.user-css .userstates .line-spacer {
    display: none !important;
}

.user-css .userstates [class*=user-state-] .line-spacer {
    display: block !important;
}

/* Buttons */
.user-css .btn,
.user-css a.btn,
.user-css .label-roller,
.user-css-extended .btn,
.user-css-presentation .tooltipster-content .btn {
    font-size: 1em;
    border: var(--border) !important;
    border-radius: var(--borderRadius);
    transition: all 0.5s;
}

.user-css .btn:hover,
.user-css a.btn:hover,
.user-css .label-roller:hover,
.user-css-extended .btn:hover,
.user-css-presentation .tooltipster-content .btn:hover {
    transition: all 0.2s;
}

.user-css .btn:focus,
.user-css a.btn:focus,
.user-css .label-roller:focus,
.user-css-extended .btn:focus,
.user-css-presentation .btn:focus {
    box-shadow: none;
}

.user-css .btn-primary,
.user-css a.btn-primary,
.user-css-extended .btn-primary,
.user-css-presentation .tooltipster-content .btn-primary,
.user-css-presentation .tooltipster-content .btn-secondary {
    background-color: var(--titleColour);
    border-color: var(--titleColour)!important;
    color: var(--fontColourLight) !important;
}

.user-css .btn-primary:hover,
.user-css a.btn-primary:hover,
.user-css a.btn-primary:hover b,
.user-css-extended .btn-primary:hover,
.user-css-presentation .tooltipster-content .btn-primary:hover,
.user-css-presentation .tooltipster-content .btn-secondary:hover {
    background-color: var(--headerColour);
    border-color: var(--headerColour)!important;
    color: var(--fontColourLight) !important;
}

.user-css .btn.btn-success {
    background-color: var(--fontColourLight)!important;
    border-color: var(--fontColour) !important;
    color: var(--fontColour)!important;
}

.user-css .btn-success:hover {
    background-color: var(--fontColour)!important;
    color: var(--fontColourLight)!important;
}

.user-css .btn-danger {
    background-color: var(--fontColourLight);
    border-color: var(--headerColour);
    color: var(--headerColour)!important;
}

.user-css .btn-danger:hover {
    background-color: var(--headerColour);
    color: var(--fontColourLight)!important;
}

.user-css .btn-info {
    background-color: var(--fontColourLight);
    border-color: var(--titleColour);
    color: var(--titleColour)!important;
}

.user-css .btn-info:hover {
    background-color: var(--titleColour);
    color: var(--fontColourLight)!important;
}

/* Quote & Aloud */
.user-css blockquote,
.user-css .aloud,
.user-css-extended blockquote,
.user-css-extended .aloud,
.user-css-presentation .tooltipster-content blockquote,
.user-css-presentation .tooltipster-content .aloud {
    border: 0.5px dotted var(--highlightColourTransparent);
    border-left: 4px solid var(--highlightColourTransparent);
    border-bottom-right-radius: var(--borderRadius);
    border-top-right-radius: var(--borderRadius);
    box-shadow: 5px 4px 14px #121212;
    color: var(--fontColourLight);
    padding: var(--paddingBase);
}

.user-css blockquote, 
.user-css-extended blockquote,
.user-css-presentation .tooltipster-content blockquote {
    margin: var(--paddingBase);
    background: #092409b3;
}

.user-css .aloud,
.user-css-extended .aloud,
.user-css-presentation .tooltipster-content .aloud {
    margin-top: var(--paddingBase);
    margin-bottom: var(--paddingBase);
    background: #13461183;

}

.user-css blockquote .author,
.user-css-extended blockquote .author,
.user-css-presentation .tooltipster-content blockquote .author {
    font-style: italic;
    text-align: right;
    color: var(--highlightColour);
    margin-top: calc(var(--paddingBase)/2);
}

/* Sidebar */
.user-css .panel,
.user-css-extended .panel {
    background-color: #ffffff60;
    border-radius: 10px;

    .user-css-image-thumbnail {
        margin-top: 0px;
        margin-bottom: var(--paddingBase);
        box-shadow: none;
        border: none;
        border-radius: 10px;
    }
    .text-center {
        padding-top: calc(var(--paddingBase)/2);
        padding-bottom: calc(var(--paddingBase)/2);
    }
    .section-title {
        padding-top: var(--paddingBase);
    }

    :is(strong),
    :is(b) {
        color: var(--headerColour);
    }
}

/* Images */
.user-css .user-css-image-thumbnail {
    background: #ffffff60;
    font-style: normal;
    box-shadow: 1px 5px 10px #212121;
    margin-top: calc(var(--paddingBase)/2);
    margin-bottom: calc(var(--paddingBase)/2);
    margin-left: auto;
    margin-right: auto;
    padding: var(--paddingBase);
    border: 0.5px dotted var(--titleColour);
    border-radius: var(--borderRadius);

    img {
        object-fit: contain;
        width: 100%!important;
        min-width: 10px!important;
    }
}

.user-css .artist-credits {
    text-align: center;
    padding-top: 10px;
    font-size: 0.9em;
}

/* Spoilers */
.user-css .spoiler-card,
.user-css-extended .spoiler-content,
.user-css-presentation .tooltipster-content .spoiler-content {
    background: #03086160;
    border: 0.5px dotted #03086190;
    border-left: 4px solid #030861;
    border-radius: 0;
    border-top-right-radius: calc(var(--borderRadius)*2);
    border-bottom-right-radius: calc(var(--borderRadius)*2);
    color: var(--fontColourLight);
}

/* Article Block */
.user-css .article-panel,
.user-css .map-box .map-thumbnail img,
.user-css-extended .article-panel,
.user-css-presentation .tooltipster-content .article-panel {
    background-color: #cccccc60;
    border: 4px solid var(--titleColour);
    border-radius: 15px 225px 15px 255px/225px 15px 255px 15px;

    .cover {
        margin: 0px;
        box-shadow: 4px 5px 10px #212121;
        border-radius: 15px 225px 15px 255px/225px 15px 255px 15px;
    }
    .heading {
        color: var(--titleColour);
        margin-top: calc(var(--paddingBase)*0.75);
    }
    .subheading,
    .subheading .date {
        color: var(--fontColour);
        margin-top: calc(var(--paddingBase)/2);
        margin-bottom: calc(var(--paddingBase)*0.75);
    }
    .excerpt {
        font-size: 1em;
    }
}

/* Map Box */
.user-css .map-box {
    padding: 0;
    background: transparent;

    .mb-3 {
        display: none;
    }
}

.user-css .maps-list-header {
    font-size: 1.5em;
}

/* Book Covers */
.user-css .bookcover {
    margin: 10px;
    display: inline-block;
    width: 300px;
    height: 400px;
    position: relative;
    background-color: var(--fontColourLight);
}

.user-css .booktitle {
    position: absolute;
    left: 0;
    top: 20%;
    background: #ccccccfa;
    padding: var(--paddingBase);
    font-family: var(--fontTitle);
    font-weight: 700;
    text-transform: uppercase;
    color: var(--titleColour);
}

.user-css .bookcover a:hover {
    img {
        opacity: .6;
    }
    .booktitle {
        background: rgb(19, 70, 17);
        color: var(--fontColourLight);
    }
}

/* Map Blocks */
.user-css.map-included {
    margin-top: var(--paddingBase);
    border: 5px solid var(--headerColour);
    border-radius: 15px 225px 15px 255px/225px 15px 255px 15px;
}

.user-css .map-included-navigation {
    display: flex;
    justify-content: center;
}

.user-css .map-included-navigation-title {
    float: none;
    text-align: center;
}

/* Followers Page */
.user-css .followers-counter .panel {
    background: #ffffff60;
    border-radius: calc(var(--borderRadius)*2);

    h4 {
        color: var(--fontColour);
    }
    h4 a:hover strong {
        color: var(--headerColour);
    }
}

.user-css .follower-box {
    margin: var(--paddingBase);
    border: 5px solid var(--headerColour);
    border-radius: 15px 225px 15px 255px/225px 15px 255px 15px;
    padding: var(--paddingBase);
    text-align: center;
    background: #cccccc60;

    img {
        margin: var(--paddingBase);
        max-height: calc(var(--paddingBase)*3.75);
        max-width: calc(var(--paddingBase)*3.75);
    }

    a:hover {
        color: var(--fontColour);
    }
}

.user-css.page-followers .search-form {
    display: none;
}

.user-css.page-followers .nav-strip {
    border: var(--border) var(--headerColour);
    border-radius: 15px 225px 15px 255px/225px 15px 255px 15px;
    padding: calc(var(--paddingBase)/2);
    padding-left: var(--paddingBase);
    padding-right: var(--paddingBase);
    margin-top: var(--paddingBase);
    background: #cccccc60;
    width: fit-content;

    .fas.fa-chevron-left {
        display: none;
    }
    a:hover {
        color: var(--fontColour);
    }

}

/* Top Bar */
.user-css #header {
    margin-bottom: var(--paddingBase);
    margin-left: calc(var(--bs-gutter-x)*-1);
}

.user-css .backtoworld {
    padding: 0;
    justify-content: start;
}

.user-css .backtoworld a {
    background: #940606ee;
    color: var(--fontColourLight);
    font-family: var(--fontHeader);
    font-size: 16px;
    margin-top: auto;
    margin-bottom: auto;
    padding: calc(var(--paddingBase)/2) calc(var(--paddingBase)*3.5);
    padding-left: calc(var(--paddingBase)*6);
  clip-path: polygon(0% 0%, 100% 0%, calc(100% - 30px) 50%, 100% 100%, 0% 100%);
    border: 6px solid #030861be;
    display: inline-flex;
    transition: all 0.5s;
    position: relative;
    left: 9px;
}

.user-css .backtoworld a:hover {
    padding: calc(var(--paddingBase)/2) calc(var(--paddingBase)*3.5) calc(var(--paddingBase)/2) calc(var(--paddingBase)*9);
}

/* Search Bar */
.user-css .search-form {
    position: absolute;

    .typeahead-input {
        background: var(--fontColourLight);
        color: var(--fontColour);
        border: 2px solid var(--fontColour);
        border-radius: var(--borderRadius);
        font-family: var(--fontTitle);
        width: 200px;
        text-transform: none;
        height: 30px;
        padding-left: calc(var(--paddingBase)/2);
    }
    .typeahead-input::placeholder {
            color: var(--fontColour);
            font-style: italic;
    }
}

.user-css .typeahead.dropdown-menu,
.user-css .typeahead.dropdown-menu .dropdown-item 
{
    background: var(--fontColourLight);
    border-radius: var(--borderRadius);
    color: var(--fontColour);
    font-weight: 500;
    font-size: 0.97em;

    .text-muted {
        color: var(--titleColour) !important;
    }
}

.user-css .typeahead.dropdown-menu {
    padding: 8px;

    li.active .dropdown-item:hover {
        background: #ffffff90;
    }
}

/* Copyright Footer */
.user-css .copyright-attribution {
    font-size: 0.95em;
    padding-bottom: var(--paddingBase);
    padding-left: calc(var(--paddingBase)*2);
    padding-right: calc(var(--paddingBase)*2);
}

/* Nav Tabs */
.user-css-extended .nav-tabs .nav-link,
.user-css-extended .nav-tabs .nav-link:hover,
.user-css-extended .nav-tabs .active .nav-link,
.user-css-extended .nav-tabs .active .nav-link:hover {
    border: none;
    border-radius: 0;
    font-family: var(--fontHeader);
    font-size: 0.97em;
    color: var(--fontColourLight);
}

.user-css-extended .nav-tabs .nav-link,
.user-css-extended .nav-tabs .active.nav-link:hover {
    background: var(--fontColour);
}

.user-css-extended .nav-tabs .nav-link:hover,
.user-css-extended .nav-tabs .active {
    background: var(--titleColour);
}

.user-css-extended .tab-content .tab-pane.active {
    padding: var(--paddingBase);
    margin: var(--paddingBase);
    background: #ffffff60;
    border-radius: var(--borderRadius);
}

.user-css-extended .page.article-extended-tabs .nav-tabs {
    border: none;
}

/* Comment Section */
.user-css-extended #comments h2 {
    text-align: center;
    margin-bottom: 0;
    padding-bottom: calc(var(--paddingBase)*2);
}

.user-css-extended .form-control {
    background: #f1f1f190;
    color: var(--fontColour);
    font-family: var(--fontTitle);
    font-size: 1em;
    border-radius: var(--borderRadius);
    margin-bottom: var(--paddingBase) !important;
    box-shadow: none;
    border: none;
}

.user-css-extended .comment-box {
    background: #f1f1f190;
    border-radius: var(--borderRadius);

    .comment-box-content {
        font-size: 0.9em;
    }
    .mb-3 a {
        color: var(--headerColour);
    }
    .comment-box-signature {
        border-top: 2px dotted;
        padding: var(--paddingBase);
        padding-bottom: 4px;
    }
}

/* Scrollbar */
::-webkit-scrollbar {
    width: var(--paddingBase);
    background-color: var(--fontColourLight);
}

::-webkit-scrollbar-track {
    border-radius: 10px;
}

::-webkit-scrollbar-thumb {
    background: var(--fontColour);
    border-radius: 10px;
}

/* Tags */
.user-css .label,
.user-css .timeline li .label,
.user-css-extended .label,
.user-css-extended .timeline li .label {
    display: inline-block;
    font-family: var(--fontHeader);
    font-size: 0.85em;
    background: var(--headerColour);
    padding: calc(var(--paddingBase)/2) var(--paddingBase);
    border-radius: var(--borderRadius);
    border: 2px solid var(--headerColour);
}

.user-css .label-default:hover,
.user-css .timeline li .label-default:hover,
.user-css-extended .label-default:hover,
.user-css-extended .timeline li .label-default:hover {
    background: var(--fontColourLight);
    color: var(--headerColour);
    transition: all 0.1s;
}
.user-css .label-danger,
.user-css .timeline li .label-danger,
.user-css-extended .label-danger,
.user-css-extended .timeline li .label-danger {
    background: var(--titleColour);
    border-color: var(--titleColour);
}

.user-css a.label,
.user-css-extended a.label {
    color: var(--fontColourLight);
}

/* Tables */
.user-css .table {
    color: var(--fontColour);
}

.user-css .table tr:nth-child(odd),
.user-css .table-striped tr:nth-child(odd) {
    --bs-table-accent-bg: rgba(255, 255, 255, .25);
    color: var(--fontColour);
}

.user-css .table tr:hover,
.user-css .table tr:nth-child(odd):hover,
.user-css .table-striped tr:hover,
.user-css .table-striped tr:nth-child(odd):hover {
    --bs-table-accent-bg: var(--highlightColourTransparent);
}

/* World Navigation */
.user-css-presentation .world-navigation-palette-trigger,
.user-css-presentation .backtotop-button {
    padding: var(--paddingBase);
    background: var(--fontColourLight);
    color: var(--headerColour);
    border: 5px solid var(--headerColour);
    border-radius: var(--borderRadius);
}

.user-css-presentation .world-navigation-palette-trigger:hover,
.user-css-presentation .backtotop-button:hover {
    background: var(--headerColour);
    color: var(--fontColourLight);
}

.user-css-presentation #world-navigation-sidebar {
    border-radius: var(--borderRadius);
    padding: var(--paddingBase) calc(var(--paddingBase)*2);
    font-size: 1.1em;

    ul {
        list-style-type: none;
    }
    i,
    a:hover,
    a:hover i {
        color: var(--fontColour);
    }
    small a i {
        color: var(--titleColour);
    }
    h5 {
        font-size: 1em;
    }
}

/* Hover Spoiler */
.user-css .spoilsahead,
.user-css-presentation .tooltipster-content .spoilsahead {
    position: relative;
}

.user-css .spoilcontent,
.user-css-presentation .tooltipster-content .spoilcontent {
    filter: blur(3px);
    z-index: 1;
}

.user-css .spoilcontent:hover,
.user-css-presentation .tooltipster-content .spoilcontent:hover {
    filter: blur(0px);
    z-index: 1;
    transition: 0.25s;
}

.user-css .spoilcontent:hover ~ .spoilwarning,
.user-css-presentation .tooltipster-content .spoilcontent:hover ~ .spoilwarning {
    opacity: 0%;
    transition: 0.25s;
}

.user-css .spoilwarning,
.user-css-presentation .tooltipster-content .spoilwarning {
    position: absolute;
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    width: 100%;
    top: 10%;
    z-index: 2;
    transition: 0.25s;
    font-size: 2em;
    line-height: 1em;
    pointer-events: none;
    color: var(--titleColour);
    color: var(--fontColourLight);
    background: #94060693;
    padding: var(--paddingBase);
    border-radius: var(--borderRadius);
}

/* Edwige Quotes */
.user-css .edwigeQuote {
    background: #03086190;
    padding: 2em;
    width: 100%;
    color: var(--fontColourLight);
}

.user-css .edwigePortrait .user-css-image-thumbnail {
    border: none;
    box-shadow: none;
    background: transparent;
    padding: 0;

    .artist-credits {
        padding: 0;
    }
}

.user-css .edwigeRight {
    padding-right: 3.5em;
    clip-path: polygon(3% 8%, 1% 44%, 3% 80%, 2% 96%, 22% 88%, 38% 96%, 67% 92%, 96% 98%, 94% 48%, 99% 36%, 93% 38%, 95% 7%, 73% 2%, 49% 8%, 28% 0%);
}

.user-css .edwigeLeft {
    padding-left: 4em;
    clip-path: polygon(5% 7%, 7% 38%, 1% 36%, 6% 48%, 4% 98%, 33% 92%, 62% 96%, 78% 88%, 98% 96%, 97% 80%, 99% 44%, 97% 8%, 72% 0%, 51% 8%, 27% 2%);
}

.user-css .edwigeBottom {
    clip-path: polygon(3% 8%, 1% 44%, 3% 80%, 22% 86%, 38% 84%, 50% 99%, 43% 82%, 67% 89%, 96% 78%, 94% 48%, 93% 38%, 95% 7%, 73% 2%, 49% 8%, 28% 0%);
    padding-bottom: 3em;
    padding-right: 4em;
}

.user-css .edwigeTop {
    clip-path: polygon(4% 22%, 6% 52%, 7% 62%, 5% 93%, 27% 98%, 51% 92%, 72% 100%, 97% 92%, 99% 56%, 97% 20%, 78% 14%, 62% 16%, 50% 1%, 57% 18%, 33% 11%);
    padding-top: 3em;
    padding-left: 5em;
}

.user-css .reduced-66 {
    width: 66%;
}

/* Mobile */
@media screen and (max-width: 1399px) {

    .user-css .reduced-66 {
        width: 65.9%;
    }

}

@media screen and (max-width: 1199px) {

    .user-css .reduced-66 {
        width: 65.6%;
    }
}

@media screen and (max-width: 991px) {

    .user-css :is(h1),
    .user-css-map-sidebar :is(h1),
    .user-css-extended .page :is(h1),
    .user-css-extended .orphan-era :is(h1),
    .user-css-presentation .tooltipster-content :is(h1) {
        font-size: 2.5em;
    }

    .user-css .edwigeRight {
        padding-bottom: 3em;
        padding-right: 3em;
    }

    .user-css .edwigeBottom {
        padding-bottom: 3.5em;
        padding-right: 3em;
    }

    .user-css .edwigeLeft {
        padding-top: 2em;
        padding-left: 3em;
    }

    .user-css .edwigeTop {
        padding-top: 3.5em;
        padding-left: 3em;
    }
}

@media screen and (max-width: 767px) {

    .user-css :is(h1),
    .user-css-map-sidebar :is(h1),
    .user-css-extended .page :is(h1),
    .user-css-extended .orphan-era :is(h1),
    .user-css-presentation .tooltipster-content :is(h1) {
        font-size: 2em;
    }

    .user-css-presentation .user-css-viewer {
        padding: calc(var(--paddingBase)/2);
    }

    .user-css .spoilwarning {
        align-items: flex-start;
        top: 10%;
    }

    .user-css .edwigeRight,
    .user-css .edwigeBottom {
        clip-path: polygon(3% 8%, 1% 44%, 3% 80%, 22% 86%, 38% 84%, 50% 99%, 43% 82%, 67% 89%, 96% 78%, 94% 48%, 93% 38%, 95% 7%, 73% 2%, 49% 8%, 28% 0%);
        padding-bottom: 4em;
        padding-right: 3em;
    }
    .user-css .edwigeLeft,
    .user-css .edwigeTop {
        clip-path: polygon(4% 22%, 6% 52%, 7% 62%, 5% 93%, 27% 98%, 51% 92%, 72% 100%, 97% 92%, 99% 56%, 97% 20%, 78% 14%, 62% 16%, 50% 1%, 57% 18%, 33% 11%);
        padding-top: 4em;
        padding-left: 3em;
    }
}

.user-css .row,
.user-css .user-row {
    display: flex !important;
}

.user-css .text-success {
    color: var(--headerColour) !important;
}

.user-css .contentBox.aloud {
    border: 5px solid var(--highlightColourTransparent);
