.tab-button .spoiler-button {
    line-height: 1.5;
    padding: 5px 10px;
    background: #8c0a0a;
    color: white;
    text-align: center;
    border-radius: 0px 10px 0px 0px;
    width: max-content;
    border-left: 6px solid;
    border-color: black;
}

.little-margin .spoiler-button {
    margin-top: 35px;
}

.extra-margin .spoiler-button {
    margin-top: 90px;
}

@keyframes forward {
    from { }
    to { z-index: 5;}
    }
    @keyframes back {
    from { }
    to { z-index: -10;}
}
     
.tabholder .spoiler-button {
    display: inline-block;
    line-height: 1.5;
    padding: 5px 10px;
    background: #8c0a0a;
    color: white;
    text-align: center;
    border-radius: 0px 10px 0px 0px;
    width: max-content;
    border-left: 6px solid;
    border-color: #2b2b2b;
    display: inline-block;
    font-size: 1.1em;
    margin-right: -4px;
    margin-top: 20px;
    margin-bottom: 0px;
}

.tab1,
.tab2,
.tab3,
.tab4,
.tab5,
.tab6,
.tab7,
.tab8,
.tab9,
.tab10,
.tab11,
.tab12,
.blanktab {
    animation-name: forward;
    animation-duration: 1ms;
    animation-fill-mode: forwards;
    animation-play-state: paused;
    background: #6E6A63;
    color: #f2e9da;
    padding: 15px;
    margin-top: 0px;
    width: 100%;
    position: absolute;
    display: block;
    z-index: -1;
    border-left: 6px solid #2b2b2b;
    border-radius: 0px 10px 10px;
    box-shadow: 5px 4px 14px #121212;
    font-family: Inter;
    height: 575px;
    max-height: 600px;
    overflow-y: scroll;
}

.tabholder .spoiler-button:hover,
.tabholder .spoiler-button:focus {
    padding-top: 18px;
    padding-bottom: 12px;
    margin-top: 0px;
    margin-bottom: 0px;
}

.blanktab {
    z-index: 0;
    background: #58554F !important;
}

.tab1::-webkit-scrollbar,
.tab2::-webkit-scrollbar,
.tab3::-webkit-scrollbar,
.tab4::-webkit-scrollbar,
