.tabholder {
    margin-bottom: 50px;
    min-height: 300px;
    height: 560px;
    position: relative;
    width: 100%;
}
    
.tabholder .well,
.tabholder .collapse.in,
.tabholder .collapsing,
.tabholder .card {
